    <script>
        var $ = jQuery;

        $(".apendForm").append(
            "<form class='selectFilial' name='selectFilial' method='post' action='<?=url("plans");?>' enctype='multipart/form-data'>" +
                "<input type='hidden' name='idFilial' value='<?=$f->id;?>'>"+
