            "</form>"
        );


        $(document).ready(function() {
            $('#modalUf').modal('show');
        });

        $("selectFilial").submit(function (e) {
            e.preventDefault();
            let form = $(this);
            $.ajax({
                url: form.attr("action"),
                data: form.serialize(),
                type: "POST",
                dataType: "json",

            });
        });

        $("body").on("click", "[data-action]", function (e) {
            e.preventDefault();
            let data = $(this).data();
            let div = $(this).parent();

            $.post(data.action, data, function () {
                div.fadeout();
            }, "json")
        })
    </script>
